import React, { Fragment } from 'react';
import RestaurantSearchContent from './restaurantSearch.component';
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { withToastManager } from 'react-toast-notifications';
import { useWebId } from '@inrupt/solid-react-components';
import {
    Header,
    RestaurantSearchContainer,
    RestaurantSearchWrapper
} from './restaurantSearch.style';
import * as $rdf from 'rdflib';


const loadFromUrl = (url, store) => {
  return new Promise((resolve, reject) => {
    let fetcher = new $rdf.Fetcher(store);
    try {
      fetcher.load(url).then(response => {
        resolve(response.responseText);
        // console.debug(response.responseText);
        // $rdf.parse(response.responseText, store, $rdf.sym(url).uri,"application/rdf");
      });
    } catch (err) {
      reject(err);
    }
  });
}; 

// RG - 2019-02-28
// Prepares a query by converting SPARQL into a Solid query

const prepare = (qryStr, store) => {
  return new Promise((resolve, reject) => {
    try {
      let query = $rdf.SPARQLToQuery(qryStr, false, store);
      resolve(query);
    } catch (err) {
      reject(err);
    }
  });
};

// RG - 2019-02-28
// Executes a query on the local store

const execute = (qry, store) => {
  return new Promise((resolve, reject) => {
    // console.debug("here");
    const wanted = qry.vars;
    const resultAry = [];
    store.query(
      qry,
      results => {
        // console.debug("here1");
        if (typeof results === "undefined") {
          reject("No results.");
        } else {
          let row = rowHandler(wanted, results);
          // console.debug(row);
          if (row) resultAry.push(row);
        }
      },
      {},
      () => {
        resolve(resultAry);
      }
    );
  });
}; 

// RG - 2019-02-28
// Puts query results into an array according to the projection
 
const rowHandler = (wanted, results) => {
  const row = {};
  for (var r in results) {
    let found = false;
    let got = r.replace(/^\?/, "");
    if (wanted.length) {
      for (var w in wanted) {
        if (got === wanted[w].label) {
          found = true;
          continue;
        }
      }
      if (!found) continue;
    }
    row[got] = results[r].value;
  }
  return row;
}; 

async function getData(CUPurl, date) {
  // loading new events
  let query = '';
  let store = $rdf.graph();
  loadFromUrl(CUPurl, store).then(() =>
    prepare(query, store).then(qry =>
      execute(qry, store).then(results => {
        // do things with results 
      })
    )
  );
} 


/**
 * We are using ldflex to fetch profile data from a solid pod.
 * ldflex libary is using json-LD for this reason you will see async calls
 * when we want to get a field value, why ? becuase they are expanded the data
 * this means the result will have a better format to read on Javascript.
 * for more information please go to: https://github.com/solid/query-ldflex
 */
 const RestaurantSearch = ({ ToastManager }) => {
   
    //                       webId={this.props.webId}
   
    const webId = useWebId();
    console.log(webId);
    //console.log(props.city.toString())
    if (webId !== undefined) {
        CUPurl = webId.replace('profile/card#me', '') + 'private/events#';
        CUPurl = webId;
    }
    const image = '';
    const isLoading = false;
    const name = 'sharon';
    const selectedDate = null;
        return (
            <RestaurantSearchWrapper data-testid="restaurant-component">
                <RestaurantSearchContainer>
                    {webId && (
                        <Fragment>
                            <Header>
                            </Header>
                        <RestaurantSearchContent
                            city="Melbourne"
                            state="Victoria"
                            name={name}
                            image={image}
                            isLoading={isLoading}
                            selectedDate={selectedDate}
                        />
                        </Fragment>

                    )}
                </RestaurantSearchContainer>
            </RestaurantSearchWrapper>
        );
    
};

export default withToastManager(RestaurantSearch);
